import './Edit.css'
import React, { useContext, useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { UserContext } from '../UserContext/UserContext';

const Edit = () => {

    const [users, setUsers] = useContext(UserContext);
    const { id } = useParams();
    const user = users.filter((user) => user.id == id);

    const [name, setName] = useState(user[0].name);
    const [position, setPosition] = useState(user[0].position);
    const [salary, setSalary] = useState(user[0].salary);


    const editName = (e) => {
        setName(e.target.value);
        const edited_name = name;
        user[0].name = edited_name;
    };


    const editPosition = (e) => {
        setPosition(e.target.value);
        const edited_position = position;
        user[0].poition = edited_position;
    };


    const editSalary = (e) => {
        setSalary(e.target.value);
        const edited_salary = salary;
        user[0].salary = edited_salary;
    };


    const editUser = (e) => {
        e.preventDefault();
        setUsers([...users]);
    };

    return (
        <div className="edit">
            <Form>
                <Form.Group>
                    <Form.Label>
                        <h1>ID NO: {user[0].id}</h1>
                    </Form.Label>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        type="text"
                        name="name"
                        value={name}
                        onChange={editName}
                        placeholder={user[0].name}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Position</Form.Label>
                    <Form.Control
                        type="text"
                        name="position"
                        // value={position}
                        onChange={editPosition}
                        placeholder={user[0].position}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Salary</Form.Label>
                    <Form.Control
                        type="text"
                        name="salary"
                        // value={salary}
                        onChange={editSalary}
                        placeholder={user[0].salary}
                    />
                </Form.Group>
                <Link to="/" className='btn'>
                    <Button onSubmit={() => editUser} variant="primary" type="submit" >
                        Edit User
                    </Button>
                    <Button className="action_btn" variant="info">
                        Back to Home
                    </Button>
                </Link>
            </Form>
        </div>
    );
};

export default Edit;
