import React, { createContext, useState } from "react";

const UserContext = createContext()

export const UserProvider = (props) => {
    const [users, setUsers] = useState([
        { id: 1, name: 'kalam', position: "mmmm", salary: 30000 },
        { id: 2, name: 'balam', position: "rrrr", salary: 40000 },
        { id: 3, name: 'salam', position: "ggggg", salary: 50000 },
        { id: 4, name: 'malam', position: "kkkk", salary: 60000 },
    ]);
    return ([users, setUsers])
}
